function getData() {\n       $location = "http://data.example.com/new-data.php";\n       $ch = curl_init();\n\n       curl_setopt($ch, CURLOPT_URL, $location);\n       curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n       \n       $contents = curl_exec($ch);\n       if (curl_errno($ch)) {\n           echo 'Error:' . curl_error($ch);\n       }\n       curl_close($ch);\n\n       $jsonVars = json_decode($contents);\n       return $jsonVars;\n   }