<?php\ntry {\n    // Path to your PDF file\n    $pdf_file = '/path/to/your/file.pdf';\n    \n    // Create a new Imagick object\n    $imagick = new Imagick();\n    \n    // Read the PDF file\n    $imagick->readImage($pdf_file);\n    \n    // Set the format to jpg\n    $imagick->setImageFormat('jpeg');\n    \n    // Set the resolution (optional)\n    $imagick->setResolution(150, 150);\n    \n    // Write each page as a separate jpg file\n    foreach ($imagick as $i => $page) {\n        $page->writeImage("/path/to/output/file-" . ($i+1) . ".jpg");\n    }\n    \n    // Clear the Imagick object\n    $imagick->clear();\n    $imagick->destroy();\n    \n    echo "PDF converted to JPG successfully.";\n} catch (ImagickException $e) {\n    echo 'Error: ',  $e->getMessage();\n}\n?>