// Assuming $conn is your established mysqli connection\n$stmt = $conn->prepare("INSERT INTO project (clientname, salesperson, prospect) VALUES (?, ?, ?)");\n$stmt->bind_param("sss", $clientName, $salesPer, $prospectVal);\n\nif ($stmt->execute()) {\n    // Get the last inserted ID\n    $projectID = $stmt->insert_id;\n    \n    return $projectID;\n} else {\n    // Handle the error\n    return false; // or any error handling mechanism you prefer\n}\n$stmt->close();\n$conn->close();