<?php\n$local = '0.0.0.0'; // Bind to all interfaces\n$port = 12345; // Replace with your desired port\n\n$sock = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);\nif (!$sock) {\n    die('Could not create socket: ' . socket_strerror(socket_last_error()));\n}\n\nif (!socket_bind($sock, $local, $port)) {\n    die('Could not bind to address: ' . socket_strerror(socket_last_error()));\n}\n\n// Set the socket to non-blocking mode\nsocket_set_nonblock($sock);\n\nwhile (1) {\n    $data = @socket_read($sock, 1024, PHP_BINARY_READ);\n    if ($data !== false) {\n        echo $data;\n    }\n    // Small delay to prevent CPU overload\n    usleep(100000); // 100 milliseconds\n}\n\nsocket_close($sock);\n?>