<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    if (isset($_POST['records']) && is_array($_POST['records'])) {\n        $selectedRecords = $_POST['records'];\n        foreach ($selectedRecords as $recordIndex) {\n            echo "Checkbox $recordIndex is checked.<br>";\n        }\n    } else {\n        echo "No checkboxes selected.";\n    }\n} else {\n    echo "Invalid request method.";\n}\n?>