<?php\nfunction getRowsByArticleSearch($con, $searchString, $table, $max) {\n    $searchString = '%' . $searchString . '%';\n    $recordsQuery = "SELECT ARTICLE_NO, USERNAME, ACCESSSTARTS, ARTICLE_NAME, \n                     DATE_FORMAT(STR_TO_DATE(ACCESSSTARTS, '%d/%m/%Y %k:%i:%s'), '%d %m %Y') AS shortDate \n                     FROM $table \n                     WHERE UPPER(ARTICLE_NAME) LIKE ? \n                     ORDER BY STR_TO_DATE(ACCESSSTARTS, '%d/%m/%Y %k:%i:%s') $max";\n    if ($getRecords = $con->prepare($recordsQuery)) {\n        $getRecords->bind_param("s", $searchString);\n        $getRecords->execute();\n        $result = $getRecords->get_result();\n        $rows = array();\n        while ($row = $result->fetch_assoc()) {\n            $rows[] = $row;\n        }\n        $getRecords->close();\n        return $rows;\n    }\n    return array();\n}\n\n$con = mysqli_connect("localhost", "user", "password", "db");\nif (!$con) {\n    echo "Can't connect to MySQL Server. Errorcode: " . mysqli_connect_error();\n    exit;\n}\n$con->query("SET NAMES 'utf8'");\n$brand = "o";\n$countQuery = "SELECT ARTICLE_NO FROM AUCTIONS WHERE UPPER(ARTICLE_NAME) LIKE ?";\nif ($numRecords = $con->prepare($countQuery)) {\n    $brand = '%' . $brand . '%';\n    $numRecords->bind_param("s", $brand);\n    $numRecords->execute();\n    $numRecords->store_result();\n    $rowcount = $numRecords->num_rows;\n    $numRecords->close();\n    $page_rows = 10; // Assuming page_rows is defined somewhere\n    $last = ceil($rowcount / $page_rows);\n    $rows = getRowsByArticleSearch($con, "test", "AUCTIONS", " ");\n} else {\n    echo $con->error;\n}\n\nforeach ($rows as $row) {\n    $pk = $row['ARTICLE_NO'];\n    echo '<tr>' . "\n";\n    echo '<td><a href="#" onclick="updateByPk(\'Layer2\', \'' . $pk . '\')">'.$row['USERNAME'].'</a></td>' . "\n";\n    echo '<td><a href="#" onclick="updateByPk(\'Layer2\', \'' . $pk . '\')">'.$row['shortDate'].'</a></td>' . "\n";\n    echo '<td><a href="#" onclick="deleterec(\'Layer2\', \'' . $pk . '\')">DELETE RECORD</a></td>' . "\n";\n    echo '</tr>' . "\n";\n}\n\n$con->close();\n?>