<?php\n$to = "recipient@example.com";\n$subject = "Subject";\n$message = "my message text";\n$from = "sender@example.com";\n$headers = "From: $from";\n\n// Define the boundary\n$boundary = md5(time());\n\n// Headers for attachments\n$headers .= "\r\nMIME-Version: 1.0\r\n" .\n            "Content-Type: multipart/mixed; boundary=\"{$boundary}\"";\n\n$body = "--{$boundary}\r\n" .\n        "Content-Type: text/plain; charset=\"iso-8859-1\"\r\n" .\n        "Content-Transfer-Encoding: 7bit\r\n\r\n" .\n        $message . "\r\n\r\n";\n\n// Attach first image\n$file_path1 = "path/to/image1.gif";\n$file_name1 = basename($file_path1);\n$file_size1 = filesize($file_path1);\n$handle = fopen($file_path1, "r");\n$content1 = fread($handle, $file_size1);\nfclose($handle);\n$content1 = chunk_split(base64_encode($content1));\n\n$body .= "--{$boundary}\r\n" .\n         "Content-Type: image/gif; name=\"{$file_name1}\"\r\n" .\n         "Content-Disposition: attachment; filename=\"{$file_name1}\"\r\n" .\n         "Content-Transfer-Encoding: base64\r\n\r\n" .\n         $content1 . "\r\n\r\n";\n\n// Attach second image\n$file_path2 = "path/to/image2.gif";\n$file_name2 = basename($file_path2);\n$file_size2 = filesize($file_path2);\n$handle = fopen($file_path2, "r");\n$content2 = fread($handle, $file_size2);\nfclose($handle);\n$content2 = chunk_split(base64_encode($content2));\n\n$body .= "--{$boundary}\r\n" .\n         "Content-Type: image/gif; name=\"{$file_name2}\"\r\n" .\n         "Content-Disposition: attachment; filename=\"{$file_name2}\"\r\n" .\n         "Content-Transfer-Encoding: base64\r\n\r\n" .\n         $content2 . "\r\n\r\n";\n\n// End the boundary\n$body .= "--{$boundary}--";\n\n// Send the email\nif (mail($to, $subject, $body, $headers)) {\n    echo "Email sent successfully.";\n} else {\n    echo "Failed to send email.";\n}\n?>