function isLuceneIndex($dir) {\n    // Check if the directory exists and is readable\n    if (!is_dir($dir) || !is_readable($dir)) {\n        return false;\n    }\n\n    // Define the typical Lucene index files\n    $indexFiles = ['segments', 'segments.gen'];\n\n    // Check if any of the index files exist in the directory\n    foreach ($indexFiles as $file) {\n        if (file_exists($dir . DIRECTORY_SEPARATOR . $file)) {\n            return true;\n        }\n    }\n\n    return false;\n}\n\n$luceneDir = '/path/to/your/lucene/index';\n\nif (isLuceneIndex($luceneDir)) {\n    try {\n        $newIndex = Zend_Search_Lucene::open($luceneDir);\n    } catch (Zend_Search_Lucene_Exception $e) {\n        // Handle the exception if needed\n    }\n} else {\n    $newIndex = Zend_Search_Lucene::create($luceneDir);\n}