<?php\n\nfunction replace_tags_with_includes($text) {\n    return preg_replace_callback(\n        '/\{([A-Z0-9-]+)\}/',\n        function ($matches) {\n            $filename = $matches[1] . '.php';\n            if (file_exists($filename)) {\n                return file_get_contents($filename);\n            } else {\n                return '{' . $matches[1] . '}'; // Return original tag if file does not exist\n            }\n        },\n        $text\n    );\n}\n\n// Example usage\n$input_string = "This is a {TEST} and {CONTACT-FORM} example.";\n$result = replace_tags_with_includes($input_string);\necho $result;\n?>