<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Query to check CREATE DATABASE privilege\n$sql = "SELECT * FROM INFORMATION_SCHEMA.USER_PRIVILEGES WHERE GRANTEE LIKE CONCAT('%', ?, '%') AND PRIVILEGE_TYPE = 'CREATE'";\n\n$stmt = $conn->prepare($sql);\n$grantee = "'$username'@'%'";\n$stmt->bind_param("s", $grantee);\n$stmt->execute();\n\n$result = $stmt->get_result();\n\nif ($result->num_rows > 0) {\n    // User has CREATE DATABASE privilege\n    echo "User has the privilege to create databases.";\n    // Show create database button\n} else {\n    // User does not have CREATE DATABASE privilege\n    echo "User does not have the privilege to create databases.";\n    // Hide create database button\n}\n\n$stmt->close();\n$conn->close();\n?>