<?php\n// Associative array with ids as keys and names as values\n$associativeArray = [\n    1 => 'Alice',\n    2 => 'Bob',\n    3 => 'Charlie',\n    4 => 'David',\n    // ... potentially more values\n];\n\n// Standard array with ids to compare\n$idsToCompare = [2, 4, 6, 8, 10];\n\n// Use array_flip to convert the standard array to associative array\n$flippedIds = array_flip($idsToCompare);\n\n// Perform an intersection using array_intersect_key\n$intersectedArray = array_intersect_key($associativeArray, $flippedIds);\n\n// Result\nprint_r($intersectedArray);\n?>