function custom_mysql2date($dateformatstring, $mysqlstring) {\n    // Check if the MySQL datetime is empty\n    if (empty($mysqlstring) || $mysqlstring == '0000-00-00 00:00:00') {\n        return false;\n    }\n\n    // Extract components from the MySQL datetime string\n    $year = substr($mysqlstring, 0, 4);\n    $month = substr($mysqlstring, 5, 2);\n    $day = substr($mysqlstring, 8, 2);\n    $hour = substr($mysqlstring, 11, 2);\n    $minute = substr($mysqlstring, 14, 2);\n    $second = substr($mysqlstring, 17, 2);\n\n    // Create a timestamp using mktime\n    $timestamp = mktime($hour, $minute, $second, $month, $day, $year);\n\n    // Format the timestamp using the specified format\n    return date($dateformatstring, $timestamp);\n}\n\n// Example usage\necho custom_mysql2date("H:i", "0000-00-00 13:24:00");