<?php\n   $hostname = '{imap.example.com:993/imap/ssl}INBOX';\n   $username = 'your-email@example.com';\n   $password = 'your-email-password';\n   \n   $inbox = imap_open($hostname, $username, $password) or die('Cannot connect to mail server: ' . imap_last_error());\n   \n   $emails = imap_search($inbox, 'ALL');\n   \n   if ($emails) {\n       rsort($emails);\n       \n       foreach ($emails as $email_number) {\n           $overview = imap_fetch_overview($inbox, $email_number, 0);\n           $message = imap_fetchbody($inbox, $email_number, 1.1);\n           if (empty($message)) {\n               $message = imap_fetchbody($inbox, $email_number, 1);\n           }\n   \n           echo "Subject: " . $overview[0]->subject . "<br>";\n           echo "From: " . $overview[0]->from . "<br>";\n           echo "Date: " . $overview[0]->date . "<br>";\n           echo "Message: " . $message . "<br><hr>";\n       }\n   }\n   \n   imap_close($inbox);\n   ?>