class db {\n    protected $connection;\n\n    public function __construct($dbuser = null, $dbpassword = null, $dbname = null, $dbhost = 'localhost') {\n        if ($dbuser && $dbpassword && $dbname) {\n            // Proceed with DB connection\n            $this->connection = new mysqli($dbhost, $dbuser, $dbpassword, $dbname);\n            if ($this->connection->connect_error) {\n                die("Connection failed: " . $this->connection->connect_error);\n            }\n        } else {\n            die("Database credentials are missing.");\n        }\n    }\n}