<?php\n    $email_id = "welcome@gmail.com";\n    $recipient = "recipient@example.com"; // Replace with recipient's email\n    $message = "Your email message here"; // Define your message\n    $name = "Your Name"; // Define sender's name\n    $subject = "Subject " . $name; // Subject\n    $email = "your-email@example.com"; // Define sender's email\n\n    $headers = "From: " . $name . " <" . $email . ">\r\n";\n    $headers .= "Reply-To: " . $email . "\r\n";\n    $headers .= "MIME-Version: 1.0\r\n";\n    $headers .= "Content-Type: text/plain; charset=utf-8\r\n";\n\n    if(mail($recipient, $subject, $message, $headers)) {\n        echo "Email sent successfully.";\n    } else {\n        echo "Failed to send email.";\n    }\n?>