<?php\n// Set the content type\nheader('Content-Type: image/png');\n\n// Create the image\n$im = imagecreatetruecolor(400, 200);\n\n// Set the background color\n$bg = imagecolorallocate($im, 255, 255, 255);\nimagefilledrectangle($im, 0, 0, 399, 199, $bg);\n\n// Set the text color\n$text_color = imagecolorallocate($im, 0, 0, 0);\n\n// Path to the Noto Sans font\n$font_path = 'path/to/NotoSans-Regular.ttf';\n\n// Text to be written on the image\n$text = "你好, 世界!";\n\n// Add the text to the image\nimagettftext($im, 20, 0, 10, 50, $text_color, $font_path, $text);\n\n// Output the image\nimagepng($im);\n\n// Free up memory\nimagedestroy($im);\n?>