<?php\n// Function to list all drives\nfunction listDrives() {\n    // Execute shell command to get the list of drives\n    $output = shell_exec('wmic logicaldisk get name');\n    \n    // Convert the output to an array\n    $drives = array_filter(array_map('trim', explode("\n", $output)));\n    \n    // Remove the header from the array\n    array_shift($drives);\n    \n    return $drives;\n}\n\n// Get the list of drives\n$drives = listDrives();\n\n// Display the drives\necho "Available drives:<br>";\nforeach ($drives as $drive) {\n    echo $drive . "<br>";\n}\n?>