$pattern = '/\n    ^Guo (?P<number1>\d+) Cars @(?P<name1>\w+) |\n    ^AV (?P<number2>\d+) Cars @(?P<name2>\w+) |\n    ^@(?P<name3>\w+) (?P<number3>\d+) [#]?av\n/ix';\n\npreg_match($pattern, $source, $matches);\n\nif (!empty($matches['number1']) && !empty($matches['name1'])) {\n    // First regex matched\n    $number = $matches['number1'];\n    $name = $matches['name1'];\n} elseif (!empty($matches['number2']) && !empty($matches['name2'])) {\n    // Second regex matched\n    $number = $matches['number2'];\n    $name = $matches['name2'];\n} elseif (!empty($matches['name3']) && !empty($matches['number3'])) {\n    // Third regex matched\n    $number = $matches['number3'];\n    $name = $matches['name3'];\n}