// Array of letters\n$letters = array('a', 'b', 'c');\n\n// Initialize an associative array\n$variables = array();\n\n// Loop through array and create empty arrays within the associative array\nforeach($letters as $letter) {\n    $variables[$letter] = array();\n}\n\n// Example usage\n$variables['a'][] = 'Example value for $a';\n$variables['b'][] = 'Example value for $b';\n$variables['c'][] = 'Example value for $c';\n\nprint_r($variables['a']); // Output: Array ( [0] => Example value for $a )\nprint_r($variables['b']); // Output: Array ( [0] => Example value for $b )\nprint_r($variables['c']); // Output: Array ( [0] => Example value for $c )