$query = new Zend_Search_Lucene_Search_Query_Boolean();\nforeach ($words as $word) {\n    $wildcardTerm1 = new Zend_Search_Lucene_Index_Term($word . '*', $fieldname1);\n    $wildcardQuery1 = new Zend_Search_Lucene_Search_Query_Wildcard($wildcardTerm1);\n\n    $wildcardTerm2 = new Zend_Search_Lucene_Index_Term($word . '*', $fieldname2);\n    $wildcardQuery2 = new Zend_Search_Lucene_Search_Query_Wildcard($wildcardTerm2);\n\n    // Combine the two wildcard queries into a boolean OR query\n    $multiq = new Zend_Search_Lucene_Search_Query_Boolean();\n    $multiq->addSubquery($wildcardQuery1, true);\n    $multiq->addSubquery($wildcardQuery2, true);\n\n    $query->addSubquery($multiq, true);\n}\n\n$hits = $index->find($query);