<?php\nfunction getUniqueRandomNumbers($min, $max, $count) {\n    if (($max - $min + 1) < $count) {\n        throw new Exception("Not enough unique numbers in the specified range");\n    }\n    \n    $numbers = range($min, $max);\n    shuffle($numbers);\n    \n    return array_slice($numbers, 0, $count);\n}\n\n// Example usage:\n$min = 1;\n$max = 100;\n$count = 10;\ntry {\n    $uniqueRandomNumbers = getUniqueRandomNumbers($min, $max, $count);\n    print_r($uniqueRandomNumbers);\n} catch (Exception $e) {\n    echo 'Error: ' . $e->getMessage();\n}\n?>