<?php\n$mysqli = new mysqli('localhost', 'root', '', 'mysql');\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n// Execute the first query with SQL_CALC_FOUND_ROWS\n$s1 = $mysqli->query('SELECT SQL_CALC_FOUND_ROWS * FROM db LIMIT 0, 3');\nif (!$s1) {\n    die('Query 1 Error: ' . $mysqli->error);\n}\n\n// Execute the second query to get the number of found rows\n$s2 = $mysqli->query('SELECT FOUND_ROWS()');\nif (!$s2) {\n    die('Query 2 Error: ' . $mysqli->error);\n}\n\n// Fetch the number of found rows\n$row = $s2->fetch_row();\nif ($row) {\n    printf('%d/%d', $s1->num_rows, $row[0]);\n} else {\n    echo 'Error fetching FOUND_ROWS result';\n}\n\n$mysqli->close();\n?>