<?php\n\nclass WeightCalculator { \n    private $data = array();\n    private $universe = 0; \n\n    public function add($value, $probability) { \n        if ($probability <= 0) {\n            throw new InvalidArgumentException('Probability must be greater than zero.');\n        }\n        $this->data[] = (object)[\n            'value' => $value, \n            'probability' => $probability\n        ]; \n        $this->universe += $probability; \n    } \n\n    public function get() { \n        if ($this->universe == 0) {\n            return null; \n        }\n\n        $random = random_int(1, $this->universe); \n        $cumulativeProbability = 0;\n\n        foreach ($this->data as $item) {\n            $cumulativeProbability += $item->probability;\n            if ($random <= $cumulativeProbability) {\n                return $item->value;\n            }\n        }\n\n        return null; // Should never reach here if probabilities are set correctly\n    } \n}\n\n$object = new WeightCalculator; \n$object->add('test1', 10);\n$object->add('test2', 20); \n$object->add('test3', 30); \n$object->add('test4', 40);\n\necho $object->get();\n?>