<?php\nfunction encodeUrl($input) {\n    // Use a hashing algorithm (e.g., SHA-256)\n    $hash = hash('sha256', $input, true); // The 'true' parameter returns raw binary data\n    \n    // Use Base64 encoding and make it URL-safe\n    $encoded = rtrim(strtr(base64_encode($hash), '+/', '-_'), '=');\n    \n    // Return a truncated version to make it shorter\n    return substr($encoded, 0, 10); // Adjust the length as needed\n}\n\nfunction decodeUrl($encoded) {\n    // Note: Decoding the hash back to the original URL is not feasible with this method\n    // because hashing is a one-way function. Instead, you would need to store the original\n    // URLs and their hashes in a database if you need to retrieve them later.\n    return "Decoding is not feasible with one-way hashing.";\n}\n\n// Example usage\n$input1 = "stackoverflow.com/1/";\n$input2 = "stackoverflow.com/2/";\n\n$encoded1 = encodeUrl($input1);\n$encoded2 = encodeUrl($input2);\n\necho "Encoded URL 1: " . $encoded1 . "\n";\necho "Encoded URL 2: " . $encoded2 . "\n";\n?>