// Define the path\n$thumbsDir = __DIR__ . "/../../img/thumbs/";\n$filePath = $thumbsDir . $maxWidth . "x" . $maxHeight . "_" . $filename;\n\n// Ensure the directory exists\nif (!is_dir($thumbsDir)) {\n    die('Error: Directory does not exist.');\n}\n\n// Ensure the directory is writable\nif (!is_writable($thumbsDir)) {\n    die('Error: Directory is not writable.');\n}\n\n// Print the file path for debugging\necho 'Saving thumbnail to: ' . $filePath;\n\n// Save the image\nif (!imagejpeg($tnImage, $filePath)) {\n    die('Error: Unable to save the image.');\n}\n\necho 'Thumbnail saved successfully.';