<?php\n   // cron_unlock.php\n   $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n   $timeout = 3600; // 1 hour\n\n   // Current time minus timeout period\n   $time_limit = date('Y-m-d H:i:s', time() - $timeout);\n\n   $stmt = $pdo->prepare("UPDATE your_table SET lock = false, lock_timestamp = NULL WHERE lock = true AND lock_timestamp < :time_limit");\n   $stmt->execute([':time_limit' => $time_limit]);\n   ?>