<?php\nsession_start();\n\nclass MyClass {\n    public $foo;\n\n    public function __construct($foo) {\n        $this->foo = $foo;\n    }\n\n    public function saveToSession() {\n        $_SESSION['foo'] = serialize($this->foo);\n    }\n\n    public static function getFromSession() {\n        if (isset($_SESSION['foo'])) {\n            return unserialize($_SESSION['foo']);\n        }\n        return null;\n    }\n}\n\n// Storing the object\n$myObject = new MyClass("some value");\n$myObject->saveToSession();\n\n// Retrieving the object\n$retrievedValue = MyClass::getFromSession();\necho $retrievedValue; // Outputs: some value\n?>