<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Fetch the updated_at field\n$sql = "SELECT updated_at FROM your_table WHERE id = 1";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    $row = $result->fetch_assoc();\n    $updated_at = strtotime($row['updated_at']);\n    $current_time = time();\n    $minutes_ago = ($current_time - $updated_at) / 60;\n\n    if ($minutes_ago < 30) {\n        $minutes_left = 30 - $minutes_ago;\n        echo "Updated $minutes_ago minutes ago. $minutes_left minutes left until 30 minutes.";\n    } else {\n        echo "Updated $minutes_ago minutes ago. More than 30 minutes have passed.";\n    }\n} else {\n    echo "No record found.";\n}\n\n$conn->close();\n?>