<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    $target_dir = "uploads/";\n    $target_file = $target_dir . basename($_FILES["fileToUpload"]["name"]);\n    $uploadOk = 1;\n\n    // Check if file is a actual image or fake image\n    if (move_uploaded_file($_FILES["fileToUpload"]["tmp_name"], $target_file)) {\n        echo "The file ". htmlspecialchars(basename($_FILES["fileToUpload"]["name"])). " has been uploaded.";\n\n        // Read the uploaded file\n        $fileContent = file_get_contents($target_file);\n        $delimiter = "GIF_DELIMITER"; // Same delimiter used in VB6\n        $gifParts = explode($delimiter, $fileContent);\n\n        $i = 1;\n        foreach ($gifParts as $gif) {\n            if (!empty(trim($gif))) {\n                $gifFile = $target_dir . "image" . $i . ".gif";\n                file_put_contents($gifFile, $gif);\n                $i++;\n            }\n        }\n\n        echo "GIF files have been split successfully.";\n    } else {\n        echo "Sorry, there was an error uploading your file.";\n    }\n}\n?>