<?php\n\nclass ArticleController\n{\n    protected $articleService;\n\n    public function __construct($articleService)\n    {\n        $this->articleService = $articleService;\n    }\n\n    public function show($articleId)\n    {\n        $pageData = $this->articleService->getPageData($articleId);\n        // Pass the data to the view\n        include 'views/article_view.php';\n    }\n}\n\n// Usage example:\n$articleModel = new ArticleModel();\n$userModel = new UserModel();\n$tagModel = new TagModel();\n\n$articleService = new ArticleService($articleModel, $userModel, $tagModel);\n$articleController = new ArticleController($articleService);\n\nif (isset($_GET['article'])) {\n    $articleController->show($_GET['article']);\n}\n?>