function forwardEmail($alias, $subject, $body) {\n    // Fetch the real email address from the database\n    $stmt = $pdo->prepare("SELECT real_email FROM email_aliases WHERE alias = :alias");\n    $stmt->execute(['alias' => $alias]);\n    $realEmail = $stmt->fetchColumn();\n\n    if ($realEmail) {\n        // Use PHP's mail function to forward the email\n        mail($realEmail, $subject, $body, "From: no-reply@yourdomain.com");\n    }\n}