<?php\n// Step 1: Initialize cURL session for login\n$loginUrl = 'https://example.com/login'; // URL for the login form\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $loginUrl);\ncurl_setopt($ch, CURLOPT_POST, 1);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query([\n    'username' => 'your_username',\n    'password' => 'your_password'\n]));\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_COOKIEJAR, 'cookie.txt'); // Store cookies in this file\n\n// Execute the login request\n$response = curl_exec($ch);\n\n// Step 2: Check if login was successful\nif (strpos($response, 'Login successful') === false) {\n    die('Login failed');\n}\n\n// Step 3: Use the session cookie to make subsequent requests\n$pageUrl = 'https://example.com/protected_page';\ncurl_setopt($ch, CURLOPT_URL, $pageUrl);\ncurl_setopt($ch, CURLOPT_POST, 0); // Switch to GET request\ncurl_setopt($ch, CURLOPT_COOKIEFILE, 'cookie.txt'); // Use stored cookies\n\n// Execute the request for the protected page\n$response = curl_exec($ch);\n\n// Close the cURL session\ncurl_close($ch);\n\n// Output the captured page\necho $response;\n?>