<?php\n   session_start();\n\n   if (!isset($_SESSION['stack'])) {\n       $_SESSION['stack'] = [];\n   }\n\n   if ($_SERVER['REQUEST_METHOD'] === 'POST') {\n       $action = $_POST['action'];\n       $id = $_POST['id'] ?? null;\n\n       if ($action === 'push' && $id !== null) {\n           array_push($_SESSION['stack'], $id);\n       } elseif ($action === 'clear') {\n           $_SESSION['stack'] = [];\n       }\n   }\n\n   echo json_encode($_SESSION['stack']);\n   ?>