<?php\nheader("Content-Type: application/rss+xml; charset=UTF-8");\n\n$items = [\n    [\n        'title' => 'Sample Title',\n        'link'  => 'http://example.com',\n        'description' => 'Sample description with image',\n        'image_url' => 'http://example.com/path_to_image.png'\n    ]\n];\n\necho '<?xml version="1.0" encoding="UTF-8" ?>';\necho '<rss version="2.0">';\necho '<channel>';\necho '<title>Sample RSS Feed</title>';\necho '<link>http://example.com</link>';\necho '<description>Sample RSS feed description</description>';\n\nforeach ($items as $item) {\n    echo '<item>';\n    echo '<title>' . htmlspecialchars($item['title']) . '</title>';\n    echo '<link>' . htmlspecialchars($item['link']) . '</link>';\n    echo '<description><![CDATA[' . $item['description'] . '<img src="' . $item['image_url'] . '" />]]></description>';\n    echo '</item>';\n}\n\necho '</channel>';\necho '</rss>';\n?>