<?php\n\nclass ArticleService\n{\n    protected $articleModel;\n    protected $userModel;\n    protected $tagModel;\n\n    public function __construct($articleModel, $userModel, $tagModel)\n    {\n        $this->articleModel = $articleModel;\n        $this->userModel = $userModel;\n        $this->tagModel = $tagModel;\n    }\n\n    public function getPageData($articleId)\n    {\n        $article = $this->articleModel->getArticleById($articleId);\n        $users = $this->userModel->getLoggedInUsers();\n        $tags = $this->tagModel->getTagCloud();\n\n        return [\n            'article' => $article,\n            'users' => $users,\n            'tags' => $tags,\n        ];\n    }\n}\n?>