<?php\n// Connect to the database using mysqli\n$link = new mysqli('localhost', 'root', '', 'test');\n\nif ($link->connect_error) {\n    die('Connect Error (' . $link->connect_errno . ') ' . $link->connect_error);\n}\n\n// Fetch the user data\n$sql = "SELECT id, Name, PANumber FROM new_users";\n$result = $link->query($sql);\n\nif ($result->num_rows > 0) {\n    while ($row = $result->fetch_assoc()) {\n        // Split the names\n        list($lastname, $firstname) = explode(',', $row['Name']);\n        \n        // Prepare an update statement\n        $update_stmt = $link->prepare("UPDATE new_users SET FirstName = ?, LastName = ? WHERE id = ?");\n        $update_stmt->bind_param("ssi", $firstname, $lastname, $row['id']);\n        \n        // Execute the update statement\n        if ($update_stmt->execute()) {\n            echo "Record updated successfully for ID: " . $row['id'] . "<br>";\n        } else {\n            echo "Error updating record: " . $link->error . "<br>";\n        }\n    }\n} else {\n    echo "0 results";\n}\n\n// Close the connection\n$link->close();\n?>