function replaceSymbols($text) {\n    $replacements = array(\n        '&' => '&#38;',\n        '#' => '&#35;',\n        ';' => '&#59;',\n        ' ' => '&#32;',\n        '!' => '&#33;',\n        '"' => '&#34;',\n        '$' => '&#36;',\n        '%' => '&#37;',\n        "'" => '&#39;',\n        '(' => '&#40;',\n        ')' => '&#41;',\n        '*' => '&#42;',\n        '+' => '&#43;',\n        ',' => '&#44;',\n        '-' => '&#45;',\n        '.' => '&#46;',\n        '/' => '&#47;',\n        ':' => '&#58;',\n        '<' => '&#60;',\n        '=' => '&#61;',\n        '>' => '&#62;',\n        '?' => '&#63;',\n        '[' => '&#91;',\n        '\\' => '&#92;',\n        ']' => '&#93;',\n        '^' => '&#94;',\n        '_' => '&#95;',\n        '`' => '&#96;',\n        '{' => '&#123;',\n        '|' => '&#124;',\n        '}' => '&#125;',\n        '~' => '&#126;'\n    );\n\n    foreach ($replacements as $symbol => $code) {\n        $text = str_replace($symbol, $code, $text);\n    }\n\n    return $text;\n}