<?php\n// Include the Snoopy class file\nrequire_once 'path/to/Snoopy.class.php';\n\n// Create a new instance of the Snoopy class\n$snoopy = new Snoopy;\n\n// Define the URL you want to send the GET request to\n$url = "http://example.com/your-endpoint";\n\n// Define the query parameters as an associative array\n$params = array(\n    'param1' => 'value1',\n    'param2' => 'value2',\n    'param3' => 'value3'\n);\n\n// Build the query string from the parameters\n$query_string = http_build_query($params);\n\n// Concatenate the URL and query string\n$full_url = $url . '?' . $query_string;\n\n// Fetch the URL with the GET parameters\n$snoopy->fetch($full_url);\n\n// Output the response\necho $snoopy->results;\n?>