<?php\nsession_start();\n\n// Initialize the session variable if it doesn't exist\nif (!isset($_SESSION['page_loaded'])) {\n    $_SESSION['page_loaded'] = array();\n}\n\n// Set the session variable\n$_SESSION['page_loaded']['index'] = 1;\n\n$tmp = "index";\n\n// Check if the session variable is properly set\nif (isset($_SESSION['page_loaded']['index'])) {\n    echo "A: " . $_SESSION['page_loaded']['index']; // should return 1\n} else {\n    echo "A: not set";\n}\n\nif (isset($_SESSION['page_loaded'][$tmp])) {\n    echo "B: " . $_SESSION['page_loaded'][$tmp]; // should return 1\n} else {\n    echo "B: not set";\n}\n?>