function adjust_sleep_times() {\n    do {\n        $sleep1 = rand(2, 18);\n        $sleep2 = rand(2, 18);\n        $sleep3 = rand(2, 18);\n        $sleep4 = rand(2, 18);\n        $total_sleep = $sleep1 + $sleep2 + $sleep3 + $sleep4;\n    } while ($total_sleep < 60);\n\n    return [$sleep1, $sleep2, $sleep3, $sleep4];\n}\n\nlist($sleep1, $sleep2, $sleep3, $sleep4) = adjust_sleep_times();\necho "Sleep times: $sleep1, $sleep2, $sleep3, $sleep4\n";\necho "Total sleep time: " . ($sleep1 + $sleep2 + $sleep3 + $sleep4) . "\n";\n\n// Now you can use these sleep times in your script\nsleep($sleep1);\n// Run your second script here\nsleep($sleep2);\n// Run your second script here\nsleep($sleep3);\n// Run your second script here\nsleep($sleep4);\n// Run your second script here