use Drupal\node\Entity\Node;\n\n$xml = simplexml_load_file('path_to_your_xml_file.xml');\n\nforeach ($xml->page as $page) {\n    $node = Node::create([\n        'type' => 'your_content_type', // Replace with your content type\n        'title' => (string) $page->title,\n        'uid' => 1, // or the user ID you want to assign the node to\n        'status' => 1,\n        'field_example' => [\n            'value' => (string) $page->example_field,\n        ],\n    ]);\n\n    // Save the node\n    $node->save();\n}