class UsernameToken\n{\n    public $Username;\n    public $Password;\n\n    public function __construct($username, $password)\n    {\n        $this->Username = new SoapVar($username, XSD_STRING, null, null, null, 'http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd');\n        $this->Password = new SoapVar($password, XSD_STRING, null, null, null, 'http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd');\n    }\n}\n\nclass Security\n{\n    public $UsernameToken;\n\n    public function __construct($username, $password)\n    {\n        $this->UsernameToken = new SoapVar(new UsernameToken($username, $password), SOAP_ENC_OBJECT, null, null, null, 'urn:dkWSValueObjects');\n    }\n}\n\n$client = new SoapClient("http://www.example.com/service.wsdl");\n\n$security = new Security("username", "password");\n$header = new SoapHeader(\n    "http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd",\n    "Security",\n    new SoapVar($security, SOAP_ENC_OBJECT, null, null, null, 'urn:dkWSValueObjects'),\n    true\n);\n\n$client->__setSoapHeaders(array($header));\n\ntry {\n    $result = $client->__soapCall("GetSubscriptionGroupTypes", array());\n    print_r($result);\n} catch (SoapFault $fault) {\n    echo "Error: ", $fault->faultstring;\n}