function encryptCookie($data, $key) {\n    $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length('aes-256-cbc'));\n    $encrypted = openssl_encrypt($data, 'aes-256-cbc', $key, 0, $iv);\n    $encrypted = base64_encode($encrypted . '::' . $iv);\n    return $encrypted;\n}\n\nfunction decryptCookie($data, $key) {\n    list($encrypted_data, $iv) = explode('::', base64_decode($data), 2);\n    return openssl_decrypt($encrypted_data, 'aes-256-cbc', $key, 0, $iv);\n}\n\n// Example usage\n$key = 'your-secret-key'; // Ensure this key is kept secure and is 32 bytes long for AES-256\n$cookie_data = 'your-cookie-data';\n\n$encrypted_cookie = encryptCookie($cookie_data, $key);\nsetcookie('your_cookie_name', $encrypted_cookie, time() + 3600, '/'); // Set the cookie\n\n// To decrypt the cookie data\nif (isset($_COOKIE['your_cookie_name'])) {\n    $decrypted_cookie = decryptCookie($_COOKIE['your_cookie_name'], $key);\n    // Use $decrypted_cookie as needed\n}