$xml = simplexml_load_file('path_to_your_xml_file.xml');\n\nforeach ($xml->page as $page) {\n    $node = new stdClass();\n    $node->type = 'your_content_type'; // Replace with your content type\n    node_object_prepare($node);\n    $node->title = (string) $page->title;\n    $node->language = LANGUAGE_NONE; // or your language code\n    $node->uid = 1; // or the user ID you want to assign the node to\n\n    // Add CCK fields if necessary, for example:\n    $node->field_example[LANGUAGE_NONE][0]['value'] = (string) $page->example_field;\n\n    // Save the node\n    node_save($node);\n}