// cron_job.php\n     $mysqli = new mysqli("localhost", "user", "password", "database");\n     $file = fopen('search_results.csv', 'r');\n     $mysqli->begin_transaction();\n\n     while (($line = fgetcsv($file)) !== FALSE) {\n         $stmt = $mysqli->prepare("INSERT INTO search_results (search_id, result_id) VALUES (?, ?)");\n         $stmt->bind_param("ii", $line[0], $line[1]);\n         $stmt->execute();\n     }\n\n     $mysqli->commit();\n     fclose($file);\n     unlink('search_results.csv'); // Delete the file after processing