class SomeController extends CI_Controller {\n    public function __construct() {\n        parent::__construct();\n        $this->load->model('User_Model');\n        $user_model_instance = new User_Model();\n        \n        $this->load->model('Relation_Model', 'relation_model', TRUE, $user_model_instance);\n        $relation_model_instance = new Relation_Model($user_model_instance);\n\n        $this->load->model('Reminder_Model', 'reminder_model', TRUE, $user_model_instance, $relation_model_instance);\n    }\n\n    public function someFunction() {\n        // You can now use $this->reminder_model and its methods\n        $this->reminder_model->someMethod();\n    }\n}