<?php\n$handle = fopen("path_to_your_file", "r");\n\nwhile(!feof($handle)) {\n    $progress = fread($handle, 8192);\n    print_r($progress);\n\n    // Define the regex pattern to match the percentage and filesize\n    $pattern = '/\[download\] ([0-9.]+)% of ([0-9.]+[A-Za-z]+)/';\n\n    if (preg_match($pattern, $progress, $matches)) {\n        $percentage = $matches[1];\n        $filesize = $matches[2];\n\n        // Do something with the extracted values\n        echo "Percentage: $percentage%\n";\n        echo "Filesize: $filesize\n";\n    }\n}\n\nfclose($handle);\n?>