<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    $db_host = $_POST['db_host'];\n    $db_name = $_POST['db_name'];\n    $db_user = $_POST['db_user'];\n    $db_pass = $_POST['db_pass'];\n    \n    // Validate inputs\n    if (empty($db_host) || empty($db_name) || empty($db_user) || empty($db_pass)) {\n        echo "All fields are required.";\n        exit;\n    }\n    \n    // Attempt to connect to the database\n    $conn = new mysqli($db_host, $db_user, $db_pass, $db_name);\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n    \n    // Create tables (example)\n    $sql = "CREATE TABLE IF NOT EXISTS users (\n        id INT(6) UNSIGNED AUTO_INCREMENT PRIMARY KEY,\n        username VARCHAR(30) NOT NULL,\n        password VARCHAR(255) NOT NULL,\n        reg_date TIMESTAMP\n    )";\n    \n    if ($conn->query($sql) === TRUE) {\n        // Create config file\n        $config_content = "<?php\n";\n        $config_content .= "\$db_host = '$db_host';\n";\n        $config_content .= "\$db_name = '$db_name';\n";\n        $config_content .= "\$db_user = '$db_user';\n";\n        $config_content .= "\$db_pass = '$db_pass';\n";\n        \n        file_put_contents('config.php', $config_content);\n        \n        echo "Installation successful!";\n    } else {\n        echo "Error creating table: " . $conn->error;\n    }\n    \n    $conn->close();\n} else {\n?>\n<!DOCTYPE html>\n<html>\n<body>\n    <form method="post" action="">\n        Database Host: <input type="text" name="db_host"><br>\n        Database Name: <input type="text" name="db_name"><br>\n        Database User: <input type="text" name="db_user"><br>\n        Database Password: <input type="password" name="db_pass"><br>\n        <input type="submit" value="Install">\n    </form>\n</body>\n</html>\n<?php\n}\n?>