<?php\nfunction removeLastNode($url) {\n    $doc = new DOMDocument();\n    $doc->load($url);\n\n    $root = $doc->documentElement;\n    $lastChild = $root->lastChild;\n    \n    if ($lastChild) {\n        $root->removeChild($lastChild);\n    }\n\n    $doc->save($url);\n    echo "Last node removed successfully!";\n}\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    if (isset($_POST['removeLastNode'])) {\n        removeLastNode($_POST['url']);\n    } else {\n        $url = $_POST['url'];\n        $listData = $_POST['listData'];\n\n        $doc = new DOMDocument();\n        $doc->load($url);\n\n        $newNode = $doc->createDocumentFragment();\n        $newNode->appendXML($listData);\n\n        $root = $doc->documentElement;\n        $root->appendChild($newNode);\n\n        $doc->save($url);\n\n        echo "Data added successfully!";\n    }\n}\n?>