<?php\nrequire_once('vendor/autoload.php');\n\nuse setasign\Fpdi\Fpdi;\n\n// Path to the existing PDF\n$pdfPath = 'path/to/existing.pdf';\n$emailAddress = 'user@example.com';\n\n// Create a new FPDI instance\n$pdf = new FPDI();\n\n// Add a page from the existing PDF\n$pageCount = $pdf->setSourceFile($pdfPath);\nfor ($pageNo = 1; $pageNo <= $pageCount; $pageNo++) {\n    $tplIdx = $pdf->importPage($pageNo);\n    $pdf->AddPage();\n    $pdf->useTemplate($tplIdx, 10, 10, 200);\n\n    // Set the font and position for the email address\n    $pdf->SetFont('Helvetica');\n    $pdf->SetTextColor(0, 0, 0);\n    $pdf->SetXY(10, 10); // Change coordinates as per your requirement\n    $pdf->Write(0, 'Email: ' . $emailAddress);\n}\n\n// Output the new PDF\n$pdf->Output('F', 'path/to/output.pdf');\n?>